function renderTemplate(a){var b=document.getElementById("templateCalendar").innerHTML,c=Handlebars.compile(b),d=c(a),e=document.getElementById("hb-calendar");e.innerHTML=e.innerHTML+d}function tm(a){var b=new Date(1e3*a);return b.getHours()+"/"+b.getMinutes()+"/"+b.getSeconds()+" -- "+b}function removeEvent(a,b){calendar.removeEvent(a,b),document.getElementById(a+""+b).outerHTML=""}function showNewEvent(a){document.getElementById("day").value=a,document.getElementById("eventName").value="",document.getElementById("eventTime").value="",document.getElementById("formItems").style.display="block"}function createNewEvent(){var a=document.getElementById("day"),b=document.getElementById("eventName"),c=document.getElementById("eventTime"),d=calendar.addNewEvent(a.value,b.value,c.value);if(d){var e=document.getElementById("events-"+a.value);e.innerHTML+='<li title="'+tm(c.value)+'" id="'+a.value+d+'">'+b.value+' <a href="#" class="times" onclick="removeEvent('+a.value+", "+d+')">&times</a></li>',b.value="",c.value="",a.value="",document.getElementById("formItemsMessages").innerHTML="",document.getElementById("formItems").style.display="none"}else document.getElementById("formItemsMessages").innerHTML="Please, fill both fields."}